        if (seed == 0) {
            return Aries(gazed);
        } else if (seed == 1) {
            return Sagittarius(gazed);
        } else if (seed == 2) {
            return Capricorn(gazed);
        } else if (seed == 3) {
            return Aquarius(gazed);
        } else if (seed == 4) {
            return Pisces(gazed);
        } else if (seed == 5) {
            return Scorpio(gazed);
        } else if (seed == 6) {
            return Libra(gazed);
        } else if (seed == 7) {
            return Virgo(gazed);
        } else if (seed == 8) {
            return Leo(gazed);
        } else if (seed == 9) {
            return Cancer(gazed);
        } else if (seed == 10) {
            return Gemini(gazed);
        } else if (seed == 11) {
            return Taurus(gazed);
        } else if (seed == 12) {
