pragma solidity 0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IDeco.sol";
import "../interfaces/IDecorations.sol";
import "./Constellations.sol";

contract Decorations is Ownable, IDecorations {
    struct Decoration {
        address silhouette;
        address skyMath;
        address decorationOne;
        address focus;
    }
    uint8 public constant SILHOUTTE = 1;
    uint8 public constant SKY_MATH = 2;
    uint8 public constant DECORATION_ONE = 3;
    uint8 public constant FOCUS = 4;

    address private _chainellation;

    mapping(address => uint8) private _slot;
    address[] public allDecorations;

    mapping(uint256 => Decoration) private _decorations;

    constructor(address chainellation) {
        _chainellation = chainellation;
    }

    function getAllDecorations() public view returns (address[] memory) {
        return allDecorations;
    }

    function getAvailableDecorations(
        address user
    ) public view returns (address[] memory, uint256[] memory) {
        address[] memory availableDecorations = new address[](
            allDecorations.length
        );
        uint256[] memory ids = new uint256[](allDecorations.length);

        for (uint8 i = 0; i < allDecorations.length; i++) {
            if (IDeco(allDecorations[i]).balanceOf(user) > 0) {
                availableDecorations[i] = allDecorations[i];
                ids[i] = _slot[allDecorations[i]];
            }
        }
        return (availableDecorations, ids);
    }

    function register(address _deco, uint8 spot) public onlyOwner {
        if (_slot[_deco] != 0) revert AlreadyRegistered();

        _slot[_deco] = spot;
        allDecorations.push(_deco);
    }

    function unregister(address _deco) public onlyOwner {
        _slot[_deco] = 0;

        uint256 index = 0;

        for (uint i = index; i < allDecorations.length - 1; i++) {
            if (allDecorations[i] == _deco) {
                index = i;
                break;
            }
        }

        for (uint i = index; i < allDecorations.length - 1; i++) {
            allDecorations[i] = allDecorations[i + 1];
        }
        delete allDecorations[allDecorations.length - 1];
        allDecorations.pop();
    }

    function reset() public onlyOwner {
        for (uint i = 0; i < allDecorations.length; i++) {
            _slot[allDecorations[i]] = 0;
        }
        delete allDecorations;
    }

    function setDecorationBatch(
        uint256 tokenId,
        uint8[] memory decoTypes,
        address[] memory decos,
        uint256[] memory decoIds
    ) public {
        for (uint256 i = 0; i < decoIds.length; i++) {
            setDecoration(tokenId, decoTypes[i], decos[i], decoIds[i]);
        }
    }

    function setDecoration(
        uint256 tokenId,
        uint8 decoType,
        address deco,
        uint256 decoId
    ) public {
        if (IDeco(_chainellation).ownerOf(tokenId) != msg.sender)
            revert NotOwner();

        if (IDeco(deco).ownerOf(decoId) != msg.sender) revert NotOwner();

        //check if deco is registered
        if (_slot[deco] != decoType) revert InvalidDecoType();

        // set NFT to be souldbound
        IDeco(deco).soulbind(decoId);

        if (decoType == SILHOUTTE) {
            _decorations[tokenId].silhouette = deco;
        } else if (decoType == SKY_MATH) {
            _decorations[tokenId].skyMath = deco;
        } else if (decoType == DECORATION_ONE) {
            _decorations[tokenId].decorationOne = deco;
        } else if (decoType == FOCUS) {
            _decorations[tokenId].focus = deco;
        } else {
            revert InvalidDecoType();
        }
    }

    function getFocus(
        Color.DNA memory dna,
        uint256 gazes,
        bool daytime
    ) public view returns (string memory) {
        if (_decorations[dna.tokenId].focus == address(0)) {
            uint8 starCount;

            if (starCount > 30) {
                starCount = 30;
            } else {
                starCount = (uint8)(gazes);
            }

            return
                string.concat(
                    '<g id="focus">',
                    Constellations.getConstellation(
                        dna.constellationSeed,
                        starCount
                    ),
                    "</g>"
                );
        }
        return
            IDeco(_decorations[dna.tokenId].focus).getDeco(
                dna.tokenId,
                dna,
                gazes,
                daytime
            );
    }

    function getSilhouette(
        Color.DNA memory dna,
        uint256 gazes,
        bool daytime
    ) public view returns (string memory) {
        if (_decorations[dna.tokenId].silhouette == address(0)) {
            return '<g id="silhouette"></g>';
        }
        return
            IDeco(_decorations[dna.tokenId].silhouette).getDeco(
                dna.tokenId,
                dna,
                gazes,
                daytime
            );
    }

    function getSkyMath(
        Color.DNA memory dna,
        uint256 gazes,
        bool daytime
    ) public view returns (string memory) {
        if (_decorations[dna.tokenId].skyMath == address(0)) {
            return '<g id="skymath"></g>';
        }
        return
            IDeco(_decorations[dna.tokenId].skyMath).getDeco(
                dna.tokenId,
                dna,
                gazes,
                daytime
            );
    }

    function getDecorationOne(
        Color.DNA memory dna,
        uint256 gazes,
        bool daytime
    ) public view returns (string memory) {
        if (_decorations[dna.tokenId].decorationOne == address(0)) {
            return '<g id="deco"></g>';
        }
        return
            IDeco(_decorations[dna.tokenId].decorationOne).getDeco(
                dna.tokenId,
                dna,
                gazes,
                daytime
            );
    }

    function withdraw() external onlyOwner {
        payable(address(_msgSender())).transfer(address(this).balance);
    }

    function withdrawToken(
        address _tokenContract,
        uint256 _amount
    ) external onlyOwner {
        IERC20(_tokenContract).transfer(msg.sender, _amount);
    }

    error AlreadyRegistered();
    error NotOwner();
    error InvalidDecoType();
}

interface IERC20 {
    function transfer(address _to, uint256 _amount) external returns (bool);
}
","keccak256":"0x2baea558f98a766bba2d2d45d05907ff21c02b75790c22e4861acf69256acd0d","license":"MIT"},"contracts/interfaces/IDeco.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;
import "../Color.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

abstract contract IDeco {
    mapping(uint256 => bool) public soulbound;

    function soulbind(uint256 tokenId) external {
        if (ownerOf(tokenId) != tx.origin) revert NotOwner();
        soulbound[tokenId] = true;
    }

    function getDeco(
        uint256 tokenId,
        Color.DNA memory dna,
        uint256 gazes,
        bool daytime
    ) public view virtual returns (string memory);

    function ownerOf(uint256 tokenId) public view virtual returns (address) {
        return tx.origin;
    }

    function balanceOf(address owner) public view virtual returns (uint256) {
        return 1;
    }

    function packageAsSVG(
        uint256 tokenId,
        Color.DNA memory dna,
        uint256 gazes,
        bool daytime
    ) public view returns (string memory) {
        return
            string.concat(
                '<svg viewBox="0 0 512 512" xmlns="http://www.w3.org/2000/svg">',
                getDeco(tokenId, dna, gazes, daytime),
                "</svg>"
            );
    }

    function generateCharacter(
        uint256 tokenId
    ) public view returns (string memory) {
        Color.DNA memory dna = Color.genDNA(
            tokenId,
            Color.defaultColors(tokenId)
        );
        bytes memory svg = abi.encodePacked(
            packageAsSVG(tokenId, dna, 0, true)
        );

        return
            string(
                abi.encodePacked(
                    "data:image/svg+xml;base64,",
                    Base64.encode(svg)
                )
            );
    }

    function getMetadata(
        uint256 tokenId
    ) public view virtual returns (string memory);

    function tokenURI(
        uint256 tokenId
    ) public view virtual returns (string memory) {
        bytes memory dataURI = abi.encodePacked(
            "{",
            getMetadata(tokenId),
            ",",
            '"image": "',
            generateCharacter(tokenId),
            '"',
            "}"
        );
        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(dataURI)
                )
            );
    }

    error NotOwner();
    error SoulBound();
}
